class Solution:
    def maxHeights(self, rods: List[int]) -> int:
        from collections import defaultdict

        heights = defaultdict(list)
        for i in range(len(rods)):
            heights[(i,)].append(i)

        queue = [(-5, -1)]
        while queue:
            cur_sum, cur_len = queue.pop(0)
            if cur_sum >= 5000:
                continue

            for roid, rid_len in heights.items():
                if cur_sum + rid_len < 5000:
                    heapq.heappush(queue, (cur_sum + rid_len, len(roid)))

        return max(heights.values())
