```python
class Node:
    def __init__(self, key):
        self.key = key
        self.children = {}

    def add_child(self, child):
        self.children[child.key] = child

class Cache:
    def __init__(self, max_size=100):
        self.max_size = max_size
        self.cache = {}

    def get(self, key):
        if key not in self.cache:
            raise KeyError(f'Key {key} not found in cache.')
        node = self.cache[key]
        if len(node.children) == 0:
            return None
        else:
            return min(node.children.values(), key=lambda n: n.height)

    def put(self, key, value):
        node = self.cache.get(key)
        if node is not None:
            if len(node.children) < self.max_size:
                node.add_child(value)
            else:
                min_child = min(node.children.values(), key=lambda n: n.height)
                del node.children[min_child.key]
                node.add_child(value)
        else:
            self.cache[key] = Node(key)
            self.cache[key].add_child(value)
